-- 查找產品名稱中包含URL路徑關鍵字的產品
SELECT 
    'product_name_match' as source_table,
    cpev.value as product_name,
    cpe.sku,
    cpe.entity_id,
    'Possible name match' as note
FROM catalog_product_entity cpe
JOIN catalog_product_entity_varchar cpev ON cpe.entity_id = cpev.entity_id
JOIN eav_attribute ea ON cpev.attribute_id = ea.attribute_id
WHERE ea.attribute_code = 'name'
  AND cpev.store_id = 0
  AND (
    cpev.value LIKE '%1018090%' OR
    cpev.value LIKE '%pluffy%' OR 
    cpev.value LIKE '%sydney%' OR
    cpev.value LIKE '%hermes%'
  )
LIMIT 20;
